package Operators;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// declare variable
		
		int a=22, b=10;

		// addition operator

		System.out.println("a+b =" +(a+b)); // we can also directly write ===> (a+b)

		System.out.println("a+b =" +(a+b+a-b));
		// Subtraction operator
		System.out.println("a-b =" + (a-b));

		// Multiplication operator
		System.out.println("a*b =" + (a*b));

		// Division operator
		System.out.println("a/b =" + (a/b));

		// Module operator
		System.out.println("a%b =" + (a%b)); // give output remaining value 
	}

}
