package increment_dec;

public class PrePostIncreDecre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 20;
		int b=20;  //20   21   22
		
		System.out.println(a++  +  ++a);
		System.out.println(++a);
		System.out.println(b--  -  --b);
		
	}

}
